#include <iostream>

using namespace std;

int A[100005], ctos;
long long D[100005], S[100005];

int main()
{
    scanf("%d",&ctos);
    for(int i=1; i<=ctos; i++){
        scanf("%d",&A[i]);
        S[A[i]]=S[A[i]]+A[i];
    }
    D[100000]=S[100000];
    for(int i=99999; i>0; i--){
        if(D[i+1]>D[i+2]+S[i]){
            D[i]=D[i+1];
        }
        else {
            D[i]=D[i+2]+S[i];
        }
    }
    cout<<D[1]<<"\n";
    return 0;
}
