#include <set>
#include <map>

#define Eo(x) {cerr << #x << " = " << (x) << endl;}
#define E(x) {cerr << #x << " = " << (x) << ' ';}
#define mp make_pair

using namespace std;
const int N = 100005;
char data[N];

vector<pair<char,int> > st;
int bestcnt = -1, beststart = -1, bestend = -1;
int ending[N];

void clr(int pos){
	st.clear();
	st.push_back(make_pair('*',pos-1));
}

int cntit(int from, int to){
	int res = 0;
	for (int i=from; i<=to; ++i){
		if (data[i] == '[') ++res;
	}
	return res;
}

int main(){
	scanf("%100003s", data);
	int l = strlen(data);

	clr(0);
	for (int i=0; i<l; ++i){
		switch (data[i]){
			case '(': st.push_back(mp(data[i],i)); break;
			case '[': st.push_back(mp(data[i],i)); break;
			case ')': if (st.back().first == '('){
					  st.pop_back();
					  ending[st.back().second+1] = i;
				  } else {
					  clr(i+1);
				  }
				  break;
			case ']': if (st.back().first == '['){
					  st.pop_back();
					  ending[st.back().second+1] = i;
				  } else {
					  clr(i+1);
				  }
				  break;
			default: cerr<<i<<endl; assert(0); break;
		}
	}

	for (int i=0; i<l; ++i){
		int e = ending[i];
		if (e){
			int cnt = cntit(i, e);
			if (cnt > bestcnt){
				bestcnt = cnt;
				beststart = i;
				bestend = e;
			}
			i = e;
		}
	}

	if (bestcnt == -1){
		printf("%d\n\n", 0);
	} else {
		data[bestend+1] = '\0';
		printf("%d\n%s\n", bestcnt, data+beststart);
	}

	return 0;
}
