template <class T> int sgn(T x) { return (x > 0) - (x < 0); }

template <typename T>
istream& operator>>(istream& stream, vector<T>& v) {
    for (T& i : v) stream >> i;
    return stream;
}
int caseN;

const int MAX = 200200;

const int MOD = 998244353;

bool solve() {
    int n;
    cin >> n;

    vi res(n);

    int curr = n-1;

    while (curr > 0) {
        int base;
        for (base = 0; base*base < curr; base++);
        int need = base*base-curr;
        int start = curr;
        for (int i = need; i <= start; i++) {
            res[i] = curr;
            curr--;
        }
    }
    for (int i = 0; i < n; i++) cout << res[i] << " ";
    cout << "\n";

    return false;
}


