#include <bits/stdc++.h>
using namespace std;

int y[10000],id[10010], a[1010];
char s[1010][1010];

int main() {
    int n;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int now = 0, cnt = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < a[i]; j++) {
            y[cnt] = now;
            id[cnt] = i % 2;
            if (i & 1) now++;
            else now--;
            cnt++;
        }
        if (i & 1) now--;
        else now++;
    }
    int miny = *min_element(y, y + cnt);
    int maxy = *max_element(y, y + cnt);
    for (int i = 0; i <= maxy - miny; i++) {
        for (int j = 0; j < cnt; j++) {
            s[i][j] = ' ';
        }
        s[i][cnt] = 0;
    }
    for (int i = 0; i < cnt; i++) {
        s[y[i] - miny][i] = (id[i] == 0 ? '/' : '\\');
    }
    for (int i = 0; i <= maxy - miny; i++) {
        puts(s[i]);
    }
    return 0;
}
