#include <bits/stdc++.h>
using namespace std;

int val[111];

int dfs(int x, int d) {
	if (d >= 12) {
		if (x == 0 && val[d - 2] == 0 && val[d - 1] == 0) return 1;
		return 0;
	}
	int ans = 0;
	for (int i = 0; i <= 18; i++) {
		int v = (i + ((d >= 2 && val[d - 2] >= 10) ? 1 : 0));
		val[d] = v;
		if (x % 10 == v % 10) ans += (10 - abs(i - 9)) * dfs(x / 10, d + 1);
	}
	return ans;
}
void solve() {
	int n;
	scanf("%d", &n);
	printf("%d\n", dfs(n , 0) - 2);
}

int main() {
	int T;
	scanf("%d", &T);
	for (int i = 0; i < T; i++) solve();
}
