#include <cstdio>

int main() {
  int N, K;
  scanf("%d %d", &N, &K);
  int res = 2 * N - K - 1;
  for (int i = 0; i < K; ++i) {
    int S;
    scanf("%d", &S);
    int c = -1;
    for (int j = 0; j < S; ++j) {
      int r;
      scanf("%d", &r);
      if (r == 1) {
        c = r;
      } else {
        if (r == c + 1) {
          res -= 2;
          c = r;
        }
      }
    }
  }
  printf("%d\n", res);
  return 0;
}
