#include <iostream>
using namespace std;

int n;

int pwr[1222000];
int udr[1222000];


int main(){
  cin >> n;
  for (int i=0; i<n; ++i){
    int a, b;
    cin >> a >> b;
    pwr[a]=b;
  }
  int ud=100000000;
  for (int i=0; i<1222000; ++i){
    int v=i-pwr[i]-1;
    if (v<0) udr[i]=0;
    else udr[i]=udr[v];
    if (pwr[i]) ++udr[i];
    ud=min(n-udr[i], ud);
  }
  cout << ud << endl;
}
