int dX[4] = {0, 1, 0, -1}, dY[4] = {1, 0, -1, 0};

int buff = 147;

bool close_enough(long long x1, long long y1, long long x2, long long y2)
{
	if(abs(x1) > abs(x2) - buff && abs(y1) > abs(y2) - buff)return 1;
	return 0;
}


int main()
{
	int gxp, gyp;
	scanf("%d %d",&gxp,&gyp);
	long long gx = gxp,gy = gyp;
	char s[147];
	scanf(" %s",s);
	long long dx = 0, dy = 0;
	map<char,int> m;
	m['U'] = 0;
	m['R'] = 1;
	m['D'] = 2;
	m['L'] = 3;
	for(int i=0; s[i] != 0; i++)
	{
		dx += dX[m[s[i]]];
		dy += dY[m[s[i]]];
	}

	long long a = 0, b = inf;
	while(a < b-1)
	{
		long long c = (a+b)/2;
		if(close_enough(dx*c,dy*c,gx,gy))b = c;
		else a = c;
	}

	gx -= a*dx;
	gy -= a*dy;


	long long x=0,y=0;
	for(int i=0; i<2*buff; i++)
	{
		for(int j=0; s[j] != 0; j++)
		{
			if(x == gx && y == gy)
			{
				printf("Yes\n");
				return 0;
			}
			x += dX[m[s[j]]];
			y += dY[m[s[j]]];
		}
	}
	printf("No\n");
	return 0;
}
