void solve()
{
    int n;
    cin >> n;
    rep(i, 1, n) cin >> a[i];
    if(n % 2 == 1)
    {
        NO;
        return;
    }
    sort(a + 1, a + n + 1);
    rep(i, 1, n)
        if(i & 1)
            b[i] = a[i / 2 + 1];
        else b[i] = a[n / 2 + i / 2];
    rep(i, 1, n) a[i] = b[i];
    a[0] = a[n];
    a[n + 1] = a[1];
    rep(i , 1, n)
    {
        bool ok = 0;
        if((a[i] > a[i - 1] && a[i] > a[i + 1]) || (a[i] < a[i - 1] && a[i] < a[i + 1]))
            ok = 1;
        else {
            NO;
        return;
        }
    }
    YES << '\n';
    rep(i, 1, n) cout << a[i] << ' ';
}
