#include <bits/stdc++.h>

#define ll long long
using namespace std;

int s(int n) {
    int res = 0;
    while (n) {
        res += n % 10;
        n /= 10;
    }
    return res;
}

int main() {
#ifdef __APPLE__
    freopen("input.txt", "r", stdin);
    freopen("output.txt", "w", stdout);
#endif

    int n;
    scanf("%d", &n);
    vector<int> ans;
    for (int i = max(0, n - 200); i <= n; ++i) {
        if (i + s(i) == n)
            ans.push_back(i);
    }
    printf("%d\n", (int)ans.size());
    for (int i : ans)
        printf("%d\n", i);
    return 0;
}
