#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <cmath>
#include <cctype>
using namespace std;

#define PB push_back
#define MP make_pair

const double pi     =   acos(-1.0);
const double eps    =   1e-8;

typedef long long LL;
typedef unsigned long long ULL;
typedef vector<int> VI;
typedef vector<string> VS;

const int maxn  =   500005;
const int maxm  =   26;

int f[maxn][maxm],pre[maxn][maxm],n,m;
char s[maxn];

inline void print(int i,int c)
{
    if (i==0) return;
    print(i-1,pre[i][c]);
    printf("%c",'A'+c);
}

int main()
{
    scanf("%d%d",&n,&m);
    scanf("%s",s);

    memset(f,-1,sizeof(f));
    memset(f[0],0,sizeof(f[0]));
    for (int i=0;i<n;++i){
        int mi[2];
        memset(mi,-1,sizeof(mi));
        for (int c=0;c<m;++c){
            if (mi[0]==-1 || f[i][c]<f[i][mi[0]]){
                mi[1]=mi[0];
                mi[0]=c;
            }else if (mi[1]==-1 || f[i][c]<f[i][mi[1]]){
                mi[1]=c;
            }
        }

        for (int c=0;c<m;++c){
            int delta=(s[i]-'A')!=c,src=mi[0];
            if (src==c) src=mi[1];

            if (f[i+1][c]==-1 || f[i+1][c]>f[i][src]+delta){
                f[i+1][c]=f[i][src]+delta;
                pre[i+1][c]=src;
            }
        }
    }

    int c=0;
    for (int i=0;i<m;++i)
    if (f[n][i]<f[n][c]) c=i;

    printf("%d\n",f[n][c]);
    print(n,c);

    return 0;
}
