#include<cstdio>
#include<algorithm>
using namespace std;

int T,n,m,k;
int arr[100000];
#define ll long long int

int main(void)
{
	scanf("%d",&T);
	while(T--){
		scanf("%d%d%d",&n,&m,&k);
		for(int i=0;i<k;i++)
			scanf("%d",&arr[i]);
		bool n3=0,m3=0;
		ll ns=0,ms=0;
		for(int i=0;i<k;i++){
			if(arr[i]>=n*2) ns+=arr[i]/n;
			if(arr[i]>=n*3) n3=1;
			if(arr[i]>=m*2) ms+=arr[i]/m;
			if(arr[i]>=m*3) m3=1;
		}
		if(ns>=m&&(n3==1||m%2==0))
			printf("Yes\n");
		else if(ms>=n&&(m3==1||n%2==0))
			printf("Yes\n");
		else printf("No\n");
	}
	return 0;
}
