#include<bits/stdc++.h>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

#define F first
#define S second

const int MAXN = 1e5 + 10;

int n, a[MAXN], s, f;
ll pt[MAXN];

int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	cin >> n;
	for (int i = 0; i < n; i++) cin >> a[i];
	cin >> s >> f, s--, f--;
	for (int i = 0; i < n; i++){
		int ts = s - i;
		ts = (ts%n+n)%n;
		int tf = f - i;
		tf = (tf%n+n)%n;
		if (ts < tf)
			pt[ts] += a[i], pt[tf] -= a[i];
		else
			pt[ts] += a[i], pt[0] += a[i], pt[tf] -= a[i];
	}

	ll cur = 0, ans = 0, id = -1;
	for (int i = 0; i < n; i++){
		cur += pt[i];
		if (ans < cur){
			id = i;
			ans = cur;
		}
	}
	cout << id+1 << endl;
	return 0;
}
