#include<cstdio>
#include<cstdlib>
#include<cstring>
#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;

int w3[110],w2[110];
int k,N;
int g[4000],fr[4000];
vector<int>	xl;

void init()
{
	w3[3]=1;
	for (int i=4;i<=100;i++)	w3[i]=w3[i-1]*i/(i-3);
	w2[2]=1;
	for (int i=3;i<=100;i++)	w2[i]=w2[i-1]*i/(i-2);
}

int main()
{
	init();
	cin>>k;
	int bc;
	for (int i=3;i<=90;i++)	if (w3[i+1]>k)
	{
		bc=i;
		break;
	}
	k-=w3[bc];
	memset(g,1,sizeof g);
	g[0]=0;
	for (int i=0;i<k;i++)
		for (int j=2;j<=bc;j++)
			if (i+w2[j]<=k && g[i]+j<g[i+w2[j]])
				g[i+w2[j]]=g[i]+1,fr[i+w2[j]]=j;
	N=bc+g[k];
	int now=k;
	while (now)
	{
		xl.push_back(fr[now]);
		now-=w2[fr[now]];
	}
	printf("%d\n",N);
	for (int i=0;i<N;i++)
	{
		for (int j=0;j<N;j++)
		if (i==j)	putchar('0');
		else if (i<bc && j<bc)	putchar('1');
		else
		{
			if (i<bc)
			{
				if (i<xl[j-bc])	putchar('1');
				else putchar('0');
			}
			else if (j<bc)
			{
				if (j<xl[i-bc])	putchar('1');
				else putchar('0');
			}
			else putchar('0');
		}
		puts("");
	}
	return 0;
}
