#include<cstdio>
#include<algorithm>
#include<vector>
using namespace std;
int main()
{
  int n;
  scanf("%d",&n);
  vector<int> v;
  v.resize(n);
  for(int i=0;i<v.size()-1;i++)
  {
    v[i] = i+2;
  }
  v[n-1]=1;
  for(int i=0;i<v.size();i++)
    printf("%d ",v[i]);
  printf("\n");
  return 0;
}
