#include <bits/stdc++.h>
using namespace std;

int main() {
    int n, k; cin >> n >> k;

    int c = 1, s = 1, t = k;

    while(t >= 0) {
        cout << c << ' ';
        c += s * t; t--; s *= -1;
    }
    for(int i = k + 2; i <= n; ++i) cout << i << ' ';
    cout << endl;

    return 0;
}
