#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
int n,a[60005];
int main(){
	scanf("%d",&n);
	if(n%4==1){
		printf("1\n");
		int sz=0;
		for(int i=1;i<=n;i++)
			if(i%4==1||i%4==2)a[sz++]=i;
		printf("%d ",sz);
		for(int i=0;i<sz;i++)printf("%d ",a[i]);putchar('\n');
	}else if(n%4==2){
		printf("1\n");
		int sz=0;
		a[sz++]=2;
		for(int i=3;i<=n;i++)
			if(i%4==2||i%4==3)a[sz++]=i;
		printf("%d ",sz);
		for(int i=0;i<sz;i++)printf("%d ",a[i]);putchar('\n');
	}else if(n%4==3){
		printf("0\n");
		int sz=0;
		a[sz++]=3;
		for(int i=4;i<=n;i++)
			if(i%4==1||i%4==2)a[sz++]=i;
		printf("%d ",sz);
		for(int i=0;i<sz;i++)printf("%d ",a[i]);putchar('\n');
	}else {
		printf("0\n");
		int sz=0;
		for(int i=1;i<=n;i++)
			if(i%4==2||i%4==3)a[sz++]=i;
		printf("%d ",sz);
		for(int i=0;i<sz;i++)printf("%d ",a[i]);putchar('\n');
	}
	return 0;
}
