#include <bits/stdc++.h>
using namespace std;
#define int long long
#define IOS ios::sync_with_stdio(0) , cin.tie(0) , cout.tie(0)
inline int in(){int32_t x ; scanf("%d",&x) ; return x ;}
inline long long lin(){long long x ; scanf("%lld",&x) ; return x;}
const int maxn = 1e6 + 200;
const int mod = 1e9 + 7;
const long long infll = 2e18 + 10;
const int inf = 1e9 + 10;
typedef pair<int,int> pii;
typedef long long ll;
typedef vector<int> vi;
int res , x[maxn] , suf[maxn] , pw2[maxn];
int32_t main()
{
    pw2[0] = 1;
    for (int i = 1 ; i < maxn ; i ++ ) pw2[i] = 1LL * pw2[i-1] * 2 % mod;
    int n = in();
    for (int i = 0 ; i < n ; i ++) x[i] = in();
    sort(x,x+n);
    for (int i = n - 1 ; i >= 0 ; i --)
    {
        int cntSets = (pw2[n-i-1] - 1 + mod) % mod;
        suf[i] = (2ll * suf[i + 1] + x[i]) % mod;
        res += suf[i + 1];
        if (res >= mod) res -= mod;
        res -= 1LL * cntSets * x[i] % mod;
        if (res < 0) res += mod;
    }
    cout << res << endl;
}
