using namespace std;
typedef long long ll;
typedef pair<int, int> mypair;
inline int read()
{
	int x = 0, f = 1;
	char c = getchar();
	while (!isdigit(c))
	{
		if (c == '-') f = -1;
		c = getchar();
	}
	while (isdigit(c))
	{
		x = x * 10 + c - '0';
		c = getchar();
	}
	return x * f;
}

int n, a[100005], prime[100005], pcnt = 0, mark[100005], sm[100005];

void init(int n)
{
	for (int i = 2; i <= n; i ++)
	{
		if (!mark[i]) prime[++ pcnt] = i;
		for (int j = 1; j <= pcnt; j ++)
		{
			int np = prime[j], now = np * i;
			if (now > n) break;
			mark[now] = 1;
			sm[now] = np;
			if (i % np == 0) break;
		}
	}
}

int main()
{
	n = read();
	init(n);
	int ind = 0;
	for (int i = 2; i <= n; i ++)
	{
		if (!mark[i]) a[i] = ++ ind;
		else a[i] = a[sm[i]];
	}
	for (int i = 2; i <= n; i ++)
		printf("%d%c", a[i], i == n ? 10 : 32);
}
