using namespace std;

int main() {
  int T;
  scanf("%d", &T);
  while (T-- > 0) {
    int n;
    scanf("%d", &n);
    set<int> result;
    result.insert(0);
    for (int d = 1; d * d <= n; d++) {
      int v = n / d;
      result.insert(n / v);
      result.insert(n / d);
    }
    int ans = result.size();
    printf("%d\n", ans);
    int cnt = 0;
    for (auto v : result) {
      cnt++;
      if (cnt == ans) {
        printf("%d\n", v);
      } else {
        printf("%d ", v);
      }
    }
  }
  return 0;
}
