#include <bits/stdc++.h>
using namespace std;
template<typename T = int> vector<T> create(size_t n){ return vector<T>(n); }
template<typename T, typename... Args> auto create(size_t n, Args... args){ return vector<decltype(create<T>(args...))>(n, create<T>(args...)); }
int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	long long x; cin >> x;
	vector<long long> all;
	for(long long i = 2; i * i <= x; i++){
		if(x % i == 0){
			long long cup = 1;
			while(x % i == 0) x /= i, cup *= i;
			all.push_back(cup);
		}
	}
	if(x != 1) all.push_back(x);
	int len = all.size();
	long long ans = 1ll<<60, A, B;
	for(int i = 0; i < 1<<len; i++){
		long long a = 1, b = 1;
		for(int j = 0; j < len; j++){
			if((i>>j)&1) a *= all[j];
			else b *= all[j];
		}
		if(ans > max(a, b)) A = a, B = b;
		ans = min(ans, max(a, b));
	}
	cout << A << ' ' << B << endl;
	return 0;
}
