#include<stdio.h>
#include<map>
#include<string.h>
#include<set>
#include<string>
#include<vector>
#include<algorithm>
using namespace std;
map<string,int> points;
map<string,int> goals;
map<string,int> misses;
set<string> playedWithBerland;
void parseS(char *t1,char *t2,int p1,int p2)
{
	if(p1>p2)
	{
		points[t1]+=3;
	}
	else if(p2>p1)
	{
		points[t2]+=3;
	}
	else 
	{
		points[t1]+=1;
		points[t2]+=1;
	}
	if(!strcmp(t1,"BERLAND"))
	{
		playedWithBerland.insert(t2);
	}

	if(!strcmp(t2,"BERLAND"))
	{
		playedWithBerland.insert(t1);
	}

	goals[t1]+=p1;
	goals[t2]+=p2;
	misses[t1]+=p2;
	misses[t2]+=p1;

}
char s1[100];
char s2[100];
int p1;
int p2;
char s[1000];
struct cntr
{
	string name;
	int points;
	int misses;
	int goals;
};
bool cmp(cntr &c1, cntr &c2)
{
	return c1.points>c2.points ||  
		c1.points==c2.points && c1.goals-c1.misses>c2.goals-c2.misses ||
		c1.points==c2.points && c1.goals-c1.misses == c2.goals-c2.misses && c1.goals>c2.goals ||
		c1.points==c2.points && c1.goals-c1.misses == c2.goals-c2.misses && c1.goals==c2.goals && c1.name<c2.name;


}
bool ifok(vector<cntr> & C)
{
	sort(C.begin(),C.end(),cmp);
	return C[0].name=="BERLAND" || C[1].name=="BERLAND";
}
int main()
{
	for(int i=0;i<5;i++)
	{
		gets(s);
		sscanf(s,"%s %s %d:%d",s1,s2,&p1,&p2);
		parseS(s1,s2,p1,p2);
	}

	string defC;
	for(map<string,int>::iterator it=points.begin();it!=points.end();it++)
	{
		if(it->first!="BERLAND" && playedWithBerland.find(it->first)==playedWithBerland.end())
		{
			defC=it->first;
		}
	}
	points["BERLAND"]+=3;
	vector<pair<int,int> > goodSc;
	for(int dx=1;dx<100;dx++)
	{
		for(int Y=0;Y<100;Y++)
		{
			int X=Y+dx;
			goals[defC]+=Y;
			goals["BERLAND"]+=X;
			misses[defC]+=X;
			misses["BERLAND"]+=Y;
			vector<cntr> C;
			for(map<string,int>::iterator it=points.begin();it!=points.end();it++)
			{
				cntr c1;
				c1.name=it->first;
				c1.misses=misses[it->first];
				c1.goals=goals[it->first];
				c1.points=points[it->first];
				C.push_back(c1);
			}

			if(ifok(C))
			{
				printf("%d:%d",X,Y);
				return 0;
			}
			goals[defC]-=Y;
			goals["BERLAND"]-=X;
			misses[defC]-=X;
			misses["BERLAND"]-=Y;
		}
	}
	printf("IMPOSSIBLE\n");

}
