#include <iostream>
#include <iomanip>
#include <cstdio>
#include <algorithm>
#include <cmath>
#include <set>

using namespace std;

int n, k;

int main() {
    ios::sync_with_stdio(false);

    cin >> n >> k;
    if (n <= k + k) {
        cout << -1 << endl;
        return 0;
    }
    cout << n * k << endl;
    for (int i = 0; i < n; i++)
        for (int j = 1; j <= k; j++) {
            cout << i + 1 << " " << (i + j) % n + 1 << "\n";
        }
    return 0;
}
