#include <bits/stdc++.h>
#define int long long
using namespace std;
void mermermer()
{
	int n;
	string a,b;
	cin >> n >> a >> b;
	int ans1=0,ans2=0;
	for(int i=0;i<n;i++) ans1+=a[i]!=b[i];
	reverse(a.begin(),a.end());
	for(int i=0;i<n;i++) ans2+=a[i]!=b[i];
	int ans=0,f=0,c=0;
	while(1)
	{
		if(f==0&&c>=ans1) break;
		if(f==1&&c>=ans2) break;
		if(ans%2==0) ++c;
		else f^=1;
		++ans;
	}
	cout << ans << "\n";
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--) mermermer();
	return 0;
}
