#include <iostream>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <algorithm>

#define Rep(i, a, b) for (int i(a); i <= (b); ++i)

using namespace std;

typedef long long LL;

const LL INF = 1000000000000000000;

int N, M, K, P[5033];
LL dp[5033][5033], sum[5033];

void solve() {
	scanf("%d %d %d", &N, &M, &K);
	Rep(i, 1, N) scanf("%d", P + i);

	sum[0] = 0;
	Rep(i, 1, N) sum[i] = sum[i - 1] + P[i];

	Rep(i, 0, N) Rep(j, 0, K) dp[i][j] = -INF;
	dp[0][0] = 0;
	Rep(i, 1, N) Rep(j, 0, K) {
		LL &res = dp[i][j] = dp[i - 1][j];
		if (i >= M && j >= 1)
			res = max(res, dp[i - M][j - 1] + sum[i] - sum[i - M]);
		if (res < 0) res = -INF;
	}
	cout << dp[N][K] << endl;
}

int main() {
	solve();
	return 0;
}
