#include <iostream>
#include <cassert>

using namespace std;

int main() {
    int a, b, c, d, e, f;
    cin >> a >> b >> c >> d >> e >> f;
    int res = 0, i;
    int cu = 2*a - 1;
    i = min(b,f);
    while(i) {
        cu += 2;
        res += cu;
        i--;
    }
    if(b < f) {
        assert(c > f-b && c < a+f);
        i = f-b;
        cu++;
        while(i) {
            res += cu;
            i--;
        }
        --cu;
        assert(c - f + b == e);
        i = e;
        while(i) {
            res += cu;
            cu -= 2;
            i--;
        }
    } else {
        assert(e > b-f && e < b+c);
        i = b-f;
        cu++;
        while(i) {
            res += cu;
            i--;
        }
        --cu;
        assert(e - b + f == c);
        i = c;
        while(i) {
            res += cu;
            cu -= 2;
            i--;
        }
    }
    cout << res << endl;
}
