using namespace std;

const int inf = 999999999;
const int mod = 1000000007;

inline int Getint()
{
    char ch = getchar();
    while (ch < '0' || ch > '9') ch = getchar();
    int ret = 0;
    while (ch >= '0' && ch <= '9') ret = ret * 10 + ch - '0', ch = getchar();
    return ret;
}

int a[100010], b[100010];
int n, m;

int main()
{
    n = Getint(), m = Getint();
    for (int i = 1; i <= n; i++) a[i] = Getint();
    while (!a[n]) n--;
    LL l = n + 1, r = 1000000000000000LL, ans = 0;
    while (l <= r)
    {
        for (int i = 1; i <= n; i++) b[i] = a[i];
        LL mid = l + r >> 1, rest = 0;
        int where = n;
        for (int i = 1; i <= m; i++)
        {
            rest += mid - (where + 1);
            b[where]--;
            while (1)
            {
                if (b[where] <= rest)
                {
                    rest -= b[where];
                    b[where--] = 0;
                    if (!where) break;
                }
                else
                {
                    b[where] -= rest;
                    rest = 0;
                    break;
                }
            }
            if (!where) break;
        }
        if (!where) ans = mid, r = mid - 1; else l = mid + 1;
    }
    printf("%I64d\n", ans);
    return 0;
}
