using namespace std;

const int turn[3][4] = {
    {0, 1, 2, 3},
    {0, 4, 2, 5},
    {3, 4, 1, 5}
};
int tot;
bool flag;
char c[10], C[10000][10], tmp[10];
void dfs(int a)
{
    if (flag) return;
    if (a == 3) {
        for (int i = 1; i <= tot; i++) {
            bool don = 1;
            for (int j = 0; j < 6; j++)
                if (tmp[j] != C[i][j]) {
                    don = 0;
                    break;
                }
            if (don) {
                flag = 1;
                return;
            }
        }
        return;
    }
    for (int i = 0; i <= 3; i++) {
        dfs(a + 1);
        char A = tmp[turn[a][0]];
        tmp[turn[a][0]] = tmp[turn[a][1]];
        tmp[turn[a][1]] = tmp[turn[a][2]];
        tmp[turn[a][2]] = tmp[turn[a][3]];
        tmp[turn[a][3]] = A;
    }
}
int main()
{
    for (int i = 0; i < 6; i++)
        scanf(" %c", &c[i]);
    sort(c, c + 6);
    tot = 0;
    do {
        flag = 0;
        memcpy(tmp, c, sizeof(c));
        dfs(0);
        if (!flag) {
            tot++;
            for (int i = 0; i < 6; i++)
                C[tot][i] = c[i];
        }
    } while (next_permutation(c, c + 6));
    printf("%d\n", tot);
}
