#include <algorithm>
#include <cstdio>
#include <cstdlib>
#include <cctype>
#include <cmath>
#include <iostream>
#include <queue>
#include <list>
#include <map>
#include <numeric>
#include <set>
#include <sstream>
#include <string>
#include <vector>
using namespace std;
#define VAR(a,b) __typeof(b) a=(b)
#define FOR(i,a,b) for (int _n(b), i(a); i < _n; i++)
#define FORD(i,a,b) for(int i=(a),_b=(b);i>=_b;--i)
#define FOREACH(it,c) for(VAR(it,(c).begin());it!=(c).end();++it)
#define REP(i,n) FOR(i,0,n)
#define ALL(c) (c).begin(), (c).end()
#define SORT(c) sort(ALL(c))
#define REVERSE(c) reverse(ALL(c))
#define UNIQUE(c) SORT(c),(c).resize(unique(ALL(c))-(c).begin())
#define INF 1000000000
#define X first
#define Y second
#define pb push_back
#define SZ(c) (c).size()
typedef pair<int, int> PII;
typedef vector<int> VI;
typedef vector<PII> VPII;
typedef vector<VI> VVI;

string names[] = {"Anka", "Chapay", "Cleo", "Troll", "Dracul", "Snowy", "Hexadecimal"};

int getId(string s)
{
	REP(i, 7)
		if (names[i] == s)
			return i;
	while (1);
}

VVI e;

int main()
{
	//freopen("input.txt", "r", stdin);
	//freopen("output.txt", "w+", stdout);

	e = VVI(7, VI(7));

	int t;
	cin >> t;
	while (t--)
	{
		string s1, s2, s3;
		cin >> s1 >> s2 >> s3;

		e[getId(s1)][getId(s3)] = 1;
	}

	int e1, e2, e3;

	cin >> e1 >> e2 >> e3;

	PII res (2*INF+1, 0);

	FOR (c1, 1, 7)
		FOR (c2, 1, 7-c1)
			{
				int c3 = 7 - c1 - c2;
				if (c3 <= 0)
					continue;
				VI a;
				REP (i, c1)
					a.pb (0);
				REP (i, c2)
					a.pb (1);
				REP (i, c3)
					a.pb (2);

				VI ee(3);
				ee[0] = e1/c1;
				ee[1] = e2/c2;
				ee[2] = e3/c3;

				SORT(ee);

				int diff = ee.back() -  ee[0];
				if (diff > res.X)
					continue;

				do
				{
					int like = 0;
					REP (i, 7)
						REP (j, 7)
							if (i!=j && e[i][j] && a[i] == a[j])
								like ++;

					res = min(res, PII (diff, -like));
				}
				while (next_permutation(ALL(a)));
			}

	cout << res.X << " " << -res.Y << endl;

	return 0;
}
