#include <cmath>
#include <cstdio>
#include <string>
#include <vector>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;


string	a[105], ts, st;
int	type[105], visit[105], N;
char	lucky;


char	lower(char& now)
{
	if (now >= 'A' && now <= 'Z') return now - 'A' + 'a';
	return now;
}

char	upper(char& now)
{
	if (now >= 'a' && now <= 'z') return now - 'a' + 'A';
	return now;
}

int	main()
{
//	freopen("C.in", "r", stdin);
//	freopen("C.out", "w", stdout);

	cin >> N;
	for (int i = 1; i <= N; ++ i)
	{
		cin >> a[i];
		for (int j = 0; j < a[i].size(); ++ j) a[i][j] = lower(a[i][j]);
	}
	cin >> ts;
	cin >> lucky;
	lucky = lower(lucky);

	string	st = ts;
	for (int i = 0; i < st.size(); ++ i)
	{
		type[i] = (st[i] >= 'a' && st[i] <= 'z');
		st[i] = lower(st[i]);
	}

	memset(visit, 0, sizeof(visit));
	for (int i = 0; i < st.size(); ++ i)
		for (int j = 1; j <= N; ++ j)
			if (st.size() - i >= a[j].size())
				if (st.substr(i, a[j].size()) == a[j])
					for (int k = 0; k < a[j].size(); ++ k) visit[i + k] = 1;

	for (int i = 0; i < ts.size(); ++ i) if (visit[i])
	if (lower(ts[i]) == lucky)
	{
		if (lucky == 'a')
			ts[i] = (type[i] ? 'b' : 'B');
		else
			ts[i] = (type[i] ? 'a' : 'A');
	} else
		ts[i] = (type[i] ? lower(lucky) : upper(lucky));

	cout << ts << endl;

	return 0;
}
