#include <iostream>
#include <algorithm>
#include <string>
#include <set>
#include <map>
#include <vector>

using namespace std;

string s;
int k;
int Count[26];
vector<pair<int, char> > v;

int main(){
    cin >> s;
    cin >> k;
    for(int i=0; i<s.length(); i++){
        Count[s[i]-'a']++;
    }
    for(int i=0; i<26; i++)if(Count[i])
        v.push_back(make_pair(Count[i], 'a'+i));
    sort(v.begin(), v.end());
    int ansInt = 0;
    for(int i=0; i<v.size(); i++){
        int t = min(k, v[i].first);
        k -= t;
        Count[v[i].second-'a'] = v[i].first-t;      
        if(Count[v[i].second-'a']) ansInt++;
    }
    cout << ansInt << endl;
    for(int i=0; i<s.length(); i++)if(Count[s[i]-'a']){
        Count[s[i]-'a']--;
        printf("%c", s[i]);
    }
    return 0;
}
