#include <iostream>
#define maxn 300002
#define ll long long
using namespace std;
ll n, a[maxn];
int main(){
    int t;cin>>t;
    while(t--){
        cin>>n;
        for(int i=1; i<=n; i++){
            cin>>a[i];
        }
        if(n % 2 == 1){
            cout<<"YES\n";continue;
        }
        ll diff = 0;
        for(int i=1; i<=n; i++){
            if(i % 2 == 1){
                diff -= a[i];
            }
            else{
                diff += a[i];
            }
        }
        if(diff < 0){
            cout<<"NO\n";
        }
        else{
            cout<<"YES\n";
        }
    }
    return 0;
}
