#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <iostream>
#include <algorithm>
#include <cstring>
using namespace std;

const int MAXN = 2101000;

typedef long long LL;

int n, a[MAXN];
LL CC, sum[MAXN];

int main()
{
#ifndef ONLINE_JUDGE
	freopen("input.txt", "r", stdin);
	freopen("output.txt", "w", stdout);
#endif
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i)
		scanf("%d", &a[i]), a[i] = -a[i];
	sort(a + 1, a + n + 1);
	for (int i = 1; i <= n; ++i)
		sum[i] = sum[i - 1] + a[i];
	int dep = 0, x = n;
	while (x != 1)
		++dep, x /= 4;
	for (int i = 0; i <= dep; ++i)
		CC += sum[1 << (i << 1)];
	cout << -CC << endl;
	fclose(stdin);
	fclose(stdout);
	return 0;
}
