#define eps 1e-9
int main()
{
    int n;
    cin >> n;
    double arr[2 * n];
    int stable = 0;
    double diff = 0.0;
    for(int i = 0 ; i < 2 * n ; ++i)
    {
        cin >> arr[i];
        int num = floor(arr[i]);
        if( num > arr[i] - eps && num < arr[i] + eps )
            ++stable;
        diff += arr[i] - num;
    }


    int mn = max(0, n - stable);
    int mx = min(n, 2 * n - stable);


    double ans = 1e10;
    for(int d = mn ; d <= mx ; ++d)
        ans = min(ans, fabs((double)d - diff));
    cout << fixed << setprecision(3) << ans << endl;
}
