#include<iostream>
using namespace std;
typedef long long ll;
const int P = 0x3f3f3f3f;
int _gcd(int a, int b) { if (a < b)swap(a, b); while (b)a %= b, swap(a, b); return a; }
void solve()
{
    int n, m; ll ans = 0, bas = 1;
    cin >> n >> m; n %= m; if (!n) { cout << "0\n"; return; }
    int g = _gcd(n, m), sp = m / g; while (!(sp & 1))sp >>= 1;
    if (sp != 1) { cout << "-1\n"; return; }
    while (n)g = _gcd(n, m), n /= g, m /= g, bas *= g, ans += bas * (m - 1), n--;
    cout << ans << "\n";
}
int main()
{
    ios::sync_with_stdio(0); cin.tie(0);
    int t = 1; cin >> t;
    while (t--)solve();
}
