#include <bits/stdc++.h>
#define int long long 
using namespace std;
const int INF=1e6+5;
int n,m,k;
void solve(){
	cin>>n>>m>>k;
	if (k>3) {cout<<"0\n";return ;}
	if (k==3) {
		// 6 3 
		// 
		// >n 不是 n 的倍数
		cout<<max(0ll,m-n-(m-n)/n)<<"\n"; 
	}
	else if (k==2) {
		if (n<=m) cout<<max(0ll,max(0ll,(m-n)/n)+n)<<"\n";
		else cout<<min(m,n)<<"\n";
	}
	else cout<<1<<"\n";
}
signed main()
{
	ios::sync_with_stdio(false);
	int t=0;cin>>t;
	while (t--) solve();
	return 0;
}
