using namespace std;
int main()
{
    #ifdef absi2011
    freopen("input.txt","r",stdin);
    freopen("output.txt","w",stdout);
    #endif
    int n,m,k;
    cin>>n>>m>>k;
    int i;
    long long maxans=-1;
    for (i=1;i*i<=n;i++)
    {
        int t=n/i;
        int f=k-i+1;
        if (f<0) f=0;
        if (m>f) maxans=max(maxans,(long long)m/(f+1)*t);
    }
    for (i=1;i*i<=n;i++)
    {
        int t=i;
        int f=k-n/t+1;
        if (f<0) f=0;
        if (m>f) maxans=max(maxans,(long long)m/(f+1)*t);
    }
    cout<<maxans<<endl;
    return 0; 
}
