#include<stdio.h>

__int64 la,la1;

int main()
{
	__int64 n;
	scanf("%I64d",&n);
	if(n==1)
		la=1;
	else if(n==2)
		la=2;
	else
	{
		if(n%2==1)
		{
			la=n*(n-1)*(n-2);
		}
		else
		{
			la=(n-1)*(n-2)*(n-3);

			la1=n*(n-1)*(n-2);
			la1/=2;
			if(la1>la)
				la=la1;

			la1=n*(n-1)*(n-3);
			if(n%3==0)
				la1/=3;
			if(la1>la)
				la=la1;

			la1=n*(n-1)*(n-5);
			if(n%5==0)
				la1/=5;
			if(la1>la)
				la=la1;
		}
	}
	printf("%I64d",la);
	return 0;
}
