#include<iostream>
#include<cstdio>
#include<algorithm>
#define N 300000
using namespace std;
int n;
struct node{
	int x,y;
}b[N];
int a[N],c[N];
bool cmp1(int x,int y){
	return x>y;
}
bool cmp2(const node&a,const node&b){
	return a.x<b.x;
}
int main(){
	scanf("%d",&n);
	for (int i=1;i<=n;i++)scanf("%d",&a[i]);
	for (int i=1;i<=n;i++)scanf("%d",&b[i].x),b[i].y=i;
	sort(a+1,a+n+1,cmp1);
	sort(b+1,b+n+1,cmp2);
	for (int i=1;i<=n;i++)c[b[i].y]=a[i];
	for (int i=1;i<n;i++)printf("%d ",c[i]);
	printf("%d",c[n]);
	return 0;
}
