#include <iostream>
#include <fstream>
#include <algorithm>
using namespace std;
typedef long long ll;
ll a, b, c, l;
ll maxsum;
ll answer;
int main()
{
    cin >> a >> b >> c >> l;
    maxsum=a+b+c+l;
    for (ll i=a; i<=a+l; i++)
    {
        if (b+c>i) continue;
        else
        {
            answer+=((min(i, maxsum-i)-b-c+1)*(min(i, maxsum-i)-b-c+2))/2;
        }
    }
    for (ll i=b; i<=b+l; i++)
    {
        if (a+c>i) continue;
        else
        {
            answer+=((min(i, maxsum-i)-a-c+1)*(min(i, maxsum-i)-a-c+2))/2;
        }
    }
    for (ll i=c; i<=c+l; i++)
    {
        if (b+a>i) continue;
        else
        {
            answer+=((min(i, maxsum-i)-b-a+1)*(min(i, maxsum-i)-b-a+2))/2;
        }
    }
    cout << ((l+1)*(l+2)*(l+3))/6-answer << endl;
}
