#include<cstdio>
#include<iostream>
#include<cstring>
using namespace std;
const int N=1e5+10;
char ss[N];

int main(){
    #ifdef DouBi
    freopen("in.cpp","r",stdin);
    #endif // DouBi
    while(scanf("%s",ss)!=EOF){
        int n=strlen(ss);
        int id=-1;
        for(int i=0;i<n;i++){
            if(ss[i]!='a'){id=i;break;}
        }
        if(id==-1){
            ss[n-1]='z';
        }
        else{
            for(int i=id;i<n;i++){
                if(ss[i]=='a')break;
                ss[i]--;
            }
        }
        printf("%s\n",ss);
    }
    return 0;
}
