#include<iostream>
using namespace std;
int a[200010];
long long sum[200010];
int main() {
	ios::sync_with_stdio(false);
	int T;
	cin>>T;
	while(T--) {
		int n,x,y;
		cin>>n>>x>>y;
		for(int i=1; i<=n; i++) {
			cin>>a[i];
			sum[i]=sum[i-1]+a[i];
		}
		long long ans=1e18,s=0;
		for(int i=0; i<=n; i++) {
			if(i) {
				s+=a[i]-a[i-1];
			}
			ans=min(ans,(long long)x*a[i]+y*(sum[n]-sum[i]-(long long)a[i]*(n-i)+s));
		}
		cout<<ans<<endl;
	}
	return 0;
}
