#include <cstdio>
#include <cstring>
using namespace std;

char s[100003];

int main()
{
    scanf("%s", s);
    int n = strlen(s);
    int i = 0;
    while(i < n && s[i] != '0')
        ++i;

    if(i == n) --i;
    for(int j = 0; j < i; ++j)
        printf("%c", s[j]);
    for(int j = i+1; j < n; ++j)
        printf("%c", s[j]);
    printf("\n");
    return 0;
}
