#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;

const int	MAX_N	= 100000 + 10;
int			N;
char		a		[MAX_N];
char		b		[MAX_N];

void Init()
{
	scanf("%s%s", a, b);
	N = strlen(a);
}

int Solve()
{
	int ans1 = 0, ans2 = 0;
	for(int i = 0; i < N; ++ i) {
		ans1 += a[i] == '4' && b[i] == '7';
		ans2 += a[i] == '7' && b[i] == '4';
	}
	return max(ans1, ans2);
}

int main()
{
	Init();
	cout << Solve() << endl;
	//system("pause");
	return 0;
}
