using namespace std;
using namespace tr1;

const int maxn=2e6+5;

long long n,sum;

int main(){
    cin>>n;
    sum=0;
    int ans=0;
    for(int i=1;i<maxn;i++){
        sum+=3LL*i-1;
        if(sum>n)break;
        if((n-sum)%3==0)ans++;
    }
    cout<<ans<<endl;
    return 0;
}
