int N, X;
int main()
{
	scanf("%d%d", &N, &X);
	if (N == 1)
	{
		printf("YES\n%d\n", X);
		return 0;
	}
	if (N == 2)
	{
		if (X == 0)
			puts("NO");
		else
			printf("YES\n0 %d\n", X);
		return 0;
	}
	if (N == 3)
	{
		if (X == 0)
			printf("YES\n1 2 3\n");
		else if (X == 1)
			printf("YES\n0 2 3\n");
		else
			printf("YES\n0 1 %d\n", X ^ 1);
		return 0;
	}
	puts("YES");
	for (int i = 0; i < N - 4; i++)
	{
		X ^= i;
		printf("%d ", i);
	}
	if (1 <= X && X <= 3)
	{
		for (int i = 0; i <= 10; i++)
			for (int j = 0; j <= 10; j++)
				if (i != j)
					for (int k = 0; k <= 10; k++)
						if (i != k && j != k)
							for (int l = 0; l <= 10; l++)
								if (i != l && j != l && k != l)
									if ((i ^ j ^ k ^ l) == X)
									{
										printf("%d %d %d %d\n", 262144 + i, 262144 + j, 262144 + k, 262144 + l);
										return 0;
									}
	}
	else
		printf("262144 262145 262146 %d\n", X ^ 262144 ^ 262145 ^ 262146);
	return 0;
}
