#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

ll i,j,k,n,m,t,a[10050],res=1e18;

ll chk(ll x){
	ll i,j,k;
	ll res=0,cur=0;
	for(i=x+1;i<=n;i++){
		k=cur/a[i];
		while(k*a[i]<=cur)k++;
		res+=k;
		cur=k*a[i];
	}
	cur=0;
	for(i=x-1;i>=1;i--){
		k=cur/a[i];
		while(k*a[i]<=cur)k++;
		res+=k;
		cur=k*a[i];
	}
	//printf("NMSL%d %d\n",x,res);
	return res;
}

int main(){
	ios::sync_with_stdio(0);
	cin.tie(0);cout.tie(0);
	cin>>n;
	for(i=1;i<=n;i++)cin>>a[i];
	for(i=1;i<=n;i++){
		res=min(res,chk(i));
	}
	cout<<res;
}
