#include <bits/stdc++.h>
using namespace std;
#define int long long
constexpr int inf = 1e18;
constexpr int maxn = 2e5 + 5;
constexpr int mod = 998244353;

inline void solve()
{
  int n; cin >> n; // the number of operations
  vector<int> cnt(26);
  vector<string> s(n * 2 + 1);
  for (int i = 0; i < n * 2 + 1; i ++)
  {
    cin >> s[i];
    for (char ch : s[i])
      cnt[ch - 'a'] ^= 1;
  }
  for (int i = 0; i < 26; i ++)
    if (cnt[i])
      cout << char(i + 'a') << '\n';
}

signed main()
{
  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  int T; cin >> T;
  while (T --)
  solve();
  return 0;
}
