#include <stdio.h>
int n, m, k;
int main()
{
	scanf("%d%d", &n, &k), m = n / 2;
	if(n == 1)
		printf(k ? "-1" : "1");
	else if(k < m)
		printf("-1");
	else
	{
		for(int i = 1; i < m; i++)
			printf("%d %d ", i * 2, i * 2 + 1), k--;
		int tmp = m * 2 + k - m * 2 % k;
		printf("%d %d ", tmp, tmp + k);
		if(n & 1)
			printf("1 ");
	}
	getchar(); getchar();
	return 0;
}
