#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
inline int in(){
    int x;
    scanf("%d",&x);
    return x;
}
int n;
void solve(){
    n=in();
    if(n%2==0){puts("No");return;}
    puts("Yes");
    for(int i=1,j=n+1>>1;i<=n>>1;i++,j++){
        printf("%d %d\n",i,j+n);
        printf("%d %d\n",i+n,j+1);
    }
    printf("%d %d\n",n+1>>1,n+n);
}
int main(){
    int T=in();
    while(T--)solve();
    return 0;
}
